import React, { useEffect, useState, Fragment } from "react";

// importar cliente axios
import usuarioAxios from '../../config/axios';

import Usuario from "./Usuario";

function Usuarios(){

    // Trabajar con el state 
    // usuarios = state, 
    const [usuarios, guardarUsuarios ] = useState([]);

    // Query app
    const consultarAPI = async ()=> {
        const usuariosConsulta = await usuarioAxios.get('/usuarios');

        // colocar el resultado de estate
        guardarUsuarios(usuariosConsulta.data);
    }
    // use efect es simillar a component
    useEffect( ()=> {
        consultarAPI();
    }, []);

    return (
        <Fragment>
            <h2>Usuarios</h2>

            <a href="/usuarios/nuevo" className="btn btn-verde nvo-usuarios"><i className="fas fa-plus-circle"></i>
            Nuevo Usuario
            </a>
            <ul className="listado-usuarios">
                {usuarios.map(usuario => (
                    <Usuario
                    key={usuario._id}
                    usuario={usuario}
                    />
                ))}
            </ul>
        </Fragment>
    )
}
export default Usuarios;
