import React, { useEffect, useState, Fragment } from "react";

// importar cliente axios
import usuarioAxios from '../../config/axios';
import Cuenta from "./Cuenta";

import  { Link } from 'react-router-dom';

function Cuentas(){

    // Trabajar con el state 
    // usuarios = state, 
    const [cuentas, guardarCuentas ] = useState([]);

    
    // use efect es simillar a component
    useEffect( ()=> {
        // Query app
    const consultarAPI = async ()=> {
        const cuentasConsulta = await usuarioAxios.get('/cuentas');

        // colocar el resultado de estate
        guardarCuentas(cuentasConsulta.data);
    }
        consultarAPI();
    }, [usuarios]);

    return (
        <Fragment>
            <h2>Usuarios</h2>

            <Link to={"/usuarios/nuevo"} className="btn btn-verde nvo-usuarios">
                <i className="fas fa-plus-circle"></i>
                  Nuevo Usuario
            </Link>
            <ul className="listado-usuarios">
                {usuarios.map(usuario => (
                    <Usuario
                    key={usuario._id}
                    usuario={usuario}
                    />
                ))}
            </ul>
        </Fragment>
    )
}
export default Cuentas;
